// src/components/WeatherDisplay.jsx
import React from 'react';

const WeatherDisplay = ({ weather }) => {
  // Function to determine the background image based on weather conditions
  const getBackgroundImage = () => {
    if (!weather || !weather.weather || !weather.weather[0]) return '';

    const weatherCondition = weather.weather[0].main;
    switch (weatherCondition) {
      case 'Rain':
        return 'url(/images/Rainy.jpeg)';
      case 'Clear':
        return 'url(/images/Sunny.jpeg)';
      case 'Clouds':
        return 'url(/images/Cloudy.webp)';
      case 'Snow':
        return 'url(/images/Snow.webp)';
      case 'Drizzle':
        return 'url(/images/Drizzle.avif)';
      default:
        return 'url(/images/default.avif)'; // Fallback image
    }
  };

  // Early return if weather data is not available
  if (!weather) {
    return null;
  }

  return (
    <div
      className="w-full max-w-md p-4 rounded-lg shadow-lg text-white bg-cover bg-center"
      style={{ backgroundImage: getBackgroundImage() }}
    >
      <h2 className="text-3xl font-bold mb-2">{weather.name}</h2>
      <p className="text-xl">Temperature: {weather.main.temp}Â°C</p>
      <p className="text-xl">Humidity: {weather.main.humidity}%</p>
      <p className="text-xl">Wind Speed: {weather.wind.speed} m/s</p>
      <p className="text-xl">Condition: {weather.weather[0].description}</p>
    </div>
  );
};

export default WeatherDisplay;
